import{_ as o}from"./plugin-vue_export-helper-c27b6911.js";import{r,o as s,c as l,b as t,d as e,e as i,f as n}from"./app-95480185.js";const c="/data/unisa/AdvancedAnalytic2/week11/SpatialAssociationRule.png",d="/data/unisa/AdvancedAnalytic2/week11/SpatialColocation.png",h={},p=n('<h2 id="geospatial-questions" tabindex="-1"><a class="header-anchor" href="#geospatial-questions" aria-hidden="true">#</a> Geospatial questions</h2><table><thead><tr><th>Spatial Question</th><th>Description</th></tr></thead><tbody><tr><td>Location<br>(identify)</td><td><strong>What exist at specific location?</strong><br>What object is located at 49N 123W?</td></tr><tr><td>Condition<br>(search)</td><td><strong>Where can you find specific conditions?</strong> <br> where do low income residents live?</td></tr><tr><td>Trends<br>(overlay analysis)</td><td><strong>What has changed over time?</strong><br>What is the change in green area size from 2000?</td></tr><tr><td>Routing<br>(network analysis)</td><td><strong>Which is the best way to travel?</strong><br>How can I reach a destination in the least time?</td></tr><tr><td>Patterns<br>(Query analysis)</td><td><strong>How are object distributed in geographic space?</strong><br>Does salmon streams affect bear presence?</td></tr><tr><td>Modeling<br>(building spatial models)</td><td><strong>How would conditions change in the future?</strong><br>What areas will be floored by 1m sea level rise?</td></tr></tbody></table><h2 id="spatial-queries" tabindex="-1"><a class="header-anchor" href="#spatial-queries" aria-hidden="true">#</a> Spatial Queries</h2><ul><li>Spatial Range Queries <ul><li>Find all cities within 50 miles of Paris</li><li>Query has associated region (location, boundary)</li><li>Answer includes overlapping or contained data regions</li></ul></li><li>Nearest-Neighbor Queries <ul><li>Find the 10 cities nearest to Paris</li><li>Results must be ordered by proximity</li></ul></li><li>Spatial Join Queries <ul><li>Find all cities near a lake</li><li>Join condition involves regions and proximity.</li></ul></li></ul><h2 id="spatial-data-mining" tabindex="-1"><a class="header-anchor" href="#spatial-data-mining" aria-hidden="true">#</a> Spatial Data Mining</h2><p><em><strong>Spatial Data Mining:</strong></em> the extraction of spatial patterns from both spatial and aspatial data, possibly stored in a spatial database<br><em><strong>Spatial Pattern:</strong></em> a pattern showing the interaction of two or more spatial objects or space-depending attributes according to a particular spacing or set of arrangements</p><h3 id="families-of-spatial-data-mining-patterns" tabindex="-1"><a class="header-anchor" href="#families-of-spatial-data-mining-patterns" aria-hidden="true">#</a> Families of Spatial Data Mining Patterns</h3>',7),u=t("p",null,"Hot Spot:",-1),g=t("ul",null,[t("li",null,"Which locations are unusual or share commonalities?")],-1),b={class:"hint-container info"},f=t("p",{class:"hint-container-title"},"Info",-1),m={style:{color:"red","font-weight":"bold"}},_={href:"https://www.youtube.com/watch?v=6jl9KkmgDIw",target:"_blank",rel:"noopener noreferrer"},B=n('<li><p>Spatial Interactions</p><ul><li>Co-location objects</li><li>Which subset of spatial phenomena interact?</li></ul><div class="hint-container info"><p class="hint-container-title">Info</p><p>Association rules cannot work. <span style="color:red;font-weight:bold;">Spatial Association Rule</span> (Han, 1995) works.</p></div><h4 id="spatial-association-rule-han-1995" tabindex="-1"><a class="header-anchor" href="#spatial-association-rule-han-1995" aria-hidden="true">#</a> Spatial Association Rule (Han, 1995)</h4><p><img src="'+c+'" alt="Alt text" loading="lazy"><br><strong>Input:</strong> Feature A, B, C<br><strong>Instances:</strong> A1, A2, B1, B2, C1, C2</p><ol><li>Create transactions by reference feature, e.g. C<br> (C1, B1)<br> (C2, B2)</li><li>Support (A, B) = 0<br> Support(B, C)= 2/2 = 1</li><li>Output = (B, C)</li></ol><h4 id="spatial-colocation" tabindex="-1"><a class="header-anchor" href="#spatial-colocation" aria-hidden="true">#</a> Spatial Colocation</h4><p><img src="'+d+'" alt="Alt text" loading="lazy"><br> Feature: A, B, C<br> Feature Instances: A1, A2, B1, B2, C1, C2<br> Feature subsets: (A, B), (A, C), (B, C), (A, B, C)</p><ol><li>Participation ratio(Pr):<br> Pr(A, (A, B))= fraction of A instances neighbouring feature {B} = 2/2 = 1<br> Pr(B, (A, B)) = ½</li><li>Participation index (A, B) = pi(A, B) = min{pr(A, (A,B)), pr(B, (A,B)) = min {1, ½} = ½<br> Pi(B, C) = min{pr(B, (B,C)), pr(C, (B,C))} = min {1,1,}=1</li><li>Output: (A, B), (B,C)</li></ol></li><li><p>Location Prediction</p><ul><li>Where will a phenomenon occur?</li></ul><div class="hint-container info"><p class="hint-container-title">Info</p><p>Regression cannot work</p></div></li>',2),A=t("h2",{id:"references",tabindex:"-1"},[t("a",{class:"header-anchor",href:"#references","aria-hidden":"true"},"#"),e(" References")],-1),w={href:"https://people.unisa.edu.au/thuc.le",target:"_blank",rel:"noopener noreferrer"},y={href:"https://people.unisa.edu.au/thuc.le",target:"_blank",rel:"noopener noreferrer"};function C(S,x){const a=r("ExternalLinkIcon");return s(),l("div",null,[p,t("ul",null,[t("li",null,[u,g,t("div",b,[f,t("p",null,[e("K-Means cannot work. "),t("span",m,[t("a",_,[e("DBScan"),i(a)])]),e(" works.")])])]),B]),A,t("ol",null,[t("li",null,[t("strong",null,[e("Week 10 Slides from "),t("a",w,[e("Thuc"),i(a)]),e(" (SP52023)")])]),t("li",null,[t("strong",null,[e("Week 11 Slides from "),t("a",y,[e("Thuc"),i(a)]),e(" (SP52023)")])])])])}const P=o(h,[["render",C],["__file","07.Geospatial.html.vue"]]);export{P as default};
